mapper.select:
SELECT * FROM subject_info

    WHERE

        subject_name = #{subjectInfo.subjectName}



LIMIT #{pageable.offset}, #{pageable.pageSize}

SELECT COUNT(*) FROM subject_info

    WHERE

        subject_name = #{subjectInfo.subjectName}

mapper.update:
UPDATE subject_info
subject_name = #{subjectName}, subject_difficult = #{subjectDifficult}, settle_name = #{settleName}, subject_type = #{subjectType}, subject_score = #{subjectScore}, subject_parse = #{subjectParse}, created_by = #{createdBy}, created_time = #{createdTime}, update_by = #{updateBy}, update_time = #{updateTime}, is_deleted = #{isDeleted},
WHERE id = #{id}
